import { createStore } from 'vuex'

export default createStore({
  state: {
    films:[],
    actors:[],
    add:null
  },
  getters: {
    getFilms(state){
      return state.films
    },
    getActors(state){
      return state.actors
    },
    getAllActors(state){
      return state.actors
    },
  },
  mutations: {
    setFilms(state,payload){
      state.films = payload
    },
    setActors(state,payload){
      state.actors = payload
    },
    setAdd(state,payload){
      state.add = payload
    },
    setAllActors(state,payload){
      state.allActors = payload
    }
  },
  actions: {
    async getFilms({ commit }) {
      const response = await fetch(`http://127.0.0.1:8080/films`, {
              method: 'GET',
            });
            let res = await response.json()
            console.log(res)
            commit('setFilms',res)
    },
    async getActors({ commit },payload) {
      const response = await fetch(`http://127.0.0.1:8080/actors?filmId=${payload.filmId}`, {
              method: 'GET',
            });
            let res = await response.json()
            console.log(res)
            commit('setActors',res)
    },
    async getAllActors({ commit },payload) {
      const response = await fetch(`http://127.0.0.1:8080/actors`, {
              method: 'GET',
            });
            let res = await response.json()
            console.log(res)
            commit('setAllActors',res)
    },
    async addNew({commit},payload) {

      const response = await fetch(`http://127.0.0.1:8080/films`, {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({
                film_name:payload.name,
                filming_procentage:payload.procentage,
                genre:payload.genre,
                dauration:payload.duration,
                rating:payload.rating,
                budget_num:payload.budget,
                box_office_num:payload.box_office,
                film_date:payload.date,
              }),
            });
            let res = await response.json()
            if(res.detail != "Name nust be unique"){
              alert("New film added")
              commit('setAdd',1)
            }else{
              alert("Name nust be unique")
            }

    },
  },
  modules: {
  }
})
