import { createStore } from 'vuex'

export default createStore({
  state: {
    films:[],
  },
  getters: {
    getFilms(state){
      return state.films
    },
  },
  mutations: {
    setFilms(state,payload){
      state.user = payload
    },
  },
  actions: {
  },
  modules: {
  }
})
