import { createStore } from 'vuex'

export default createStore({
  state: {
    films:[],
    actors:[]
  },
  getters: {
    getFilms(state){
      return state.films
    },
    getActors(state){
      return state.actors
    },
  },
  mutations: {
    setFilms(state,payload){
      state.films = payload
    },
    setActors(state,payload){
      state.actors = payload
    },
  },
  actions: {
    async getFilms({ commit }) {
      const response = await fetch(`http://127.0.0.1:8080/films`, {
              method: 'GET',
            });
            let res = await response.json()
            console.log(res)
            commit('setFilms',res)
    },
    async setActors({ commit },payload) {
      const response = await fetch(`http://127.0.0.1:8080/films`, {
              method: 'GET',
            });
            let res = await response.json()
            console.log(res)
            commit('setFilms',res)
    },
  },
  modules: {
  }
})
